// client/src/components/QuoteGenerator.jsx
import { useState, useEffect } from 'react';
import axios from 'axios';

function QuoteGenerator() {
    const [quote, setQuote] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const fetchQuote = async () => {
        setIsLoading(true);
        setError('');
        try {
            const response = await axios.get('/api/quote');
            setQuote(response.data.quote);
        } catch (err) {
            const message = err.response?.data?.error || 'Failed to fetch a motivational quote.';
            setError(message);
            setQuote('');
        } finally {
            setIsLoading(false);
        }
    };

    // Fetch an initial quote on component mount
    useEffect(() => {
        fetchQuote();
    }, []);

    // Extracting the quote and author
    const [quoteText, author = 'Unknown'] = quote.split('â€”');

    return (
        <div className="p-8 text-center">
            <h2 className="text-2xl font-bold mb-6 text-indigo-600">
                Your Daily Dose of Motivation
            </h2>
            
            <div className="min-h-[150px] flex flex-col justify-center items-center">
                {isLoading ? (
                    <p className="text-xl text-indigo-500 font-semibold">Loading Quote... âœ¨</p>
                ) : error ? (
                    <div className="bg-red-100 border border-red-400 text-red-700 p-4 rounded-lg">
                        <p className="font-bold">Error Generating Quote ðŸ›‘</p>
                        <p>{error}</p>
                    </div>
                ) : (
                    <blockquote className="italic text-xl sm:text-2xl text-gray-800">
                        "{quoteText ? quoteText.trim() : ''}"
                        <footer className="mt-4 text-lg font-medium text-indigo-700 not-italic">
                            â€” {author.trim()}
                        </footer>
                    </blockquote>
                )}
            </div>

            <button
                onClick={fetchQuote}
                disabled={isLoading}
                className="mt-6 px-6 py-3 bg-pink-600 text-white font-semibold rounded-full shadow-lg hover:bg-pink-700 transition-colors disabled:bg-pink-400"
            >
                {isLoading ? 'Fetching...' : 'Get New Quote'}
            </button>
        </div>
    );
}

export default QuoteGenerator;
