// client/src/components/CurrencyConverter.jsx
import { useState } from 'react';
import axios from 'axios';

function CurrencyConverter() {
    const [amount, setAmount] = useState(100);
    const [conversion, setConversion] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const handleConvert = async () => {
        const inrValue = parseFloat(amount);
        if (isNaN(inrValue) || inrValue <= 0) {
            setError('Please enter a valid positive number for INR amount.');
            setConversion(null);
            return;
        }

        setIsLoading(true);
        setError('');
        setConversion(null);

        try {
            // Pass the amount as a query parameter
            const response = await axios.get(`/api/currency?amount=${inrValue}`);
            setConversion(response.data);
        } catch (err) {
            const message = err.response?.data?.error || 'Failed to connect to the currency service.';
            setError(message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="p-8">
            <h2 className="text-2xl font-bold mb-6 text-center text-indigo-600">
                INR (₹) to USD/EUR Converter
            </h2>
            
            <div className="max-w-md mx-auto">
                <label className="block text-gray-700 font-medium mb-2">
                    Amount in INR (₹)
                </label>
                <div className="flex space-x-3">
                    <input
                        type="number"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        placeholder="e.g., 100"
                        className="flex-grow p-3 border border-gray-300 rounded-lg focus:ring-indigo-500 focus:border-indigo-500"
                        min="1"
                    />
                    <button
                        onClick={handleConvert}
                        disabled={isLoading}
                        className="px-6 py-3 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 transition-colors disabled:bg-indigo-400"
                    >
                        {isLoading ? 'Converting...' : 'Convert'}
                    </button>
                </div>
            </div>

            {error && (
                <p className="mt-4 text-center text-red-600 bg-red-50 p-3 rounded-lg border border-red-300 max-w-md mx-auto">
                    {error}
                </p>
            )}

            {conversion && (
                <div className="mt-8 grid grid-cols-2 gap-4 max-w-sm mx-auto text-center">
                    <div className="p-4 bg-green-50 rounded-lg shadow-inner">
                        <p className="text-3xl font-bold text-green-700">${conversion.usd}</p>
                        <p className="text-gray-500 mt-1">USD</p>
                    </div>
                    <div className="p-4 bg-green-50 rounded-lg shadow-inner">
                        <p className="text-3xl font-bold text-green-700">€{conversion.eur}</p>
                        <p className="text-gray-500 mt-1">EUR</p>
                    </div>
                    <p className="col-span-2 text-sm text-gray-500 mt-2">
                        **{conversion.inr} INR** converted at current rates.
                    </p>
                </div>
            )}
            
            {!conversion && !error && !isLoading && (
                <p className="mt-8 text-center text-gray-500">
                    Enter an amount and click **Convert** to see the results.
                </p>
            )}
        </div>
    );
}

export default CurrencyConverter;
