// client/src/components/WeatherModule.jsx
import { useState, useEffect } from 'react';
import axios from 'axios';

function WeatherModule() {
    const [weatherData, setWeatherData] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchWeather = async () => {
            setIsLoading(true);
            setError('');
            try {
                // The Vite proxy handles routing '/api/weather' to http://localhost:3001
                const response = await axios.get('/api/weather'); 
                setWeatherData(response.data);
            } catch (err) {
                // Display the error message from the backend
                const message = err.response?.data?.error || 'Failed to connect to the weather service.';
                setError(message);
                setWeatherData(null);
            } finally {
                setIsLoading(false);
            }
        };

        fetchWeather();
    }, []);

    // --- Conditional Rendering ---
    if (isLoading) {
        return <div className="p-6 text-center text-indigo-500 font-semibold">Loading Weather... ‚è≥</div>;
    }

    if (error) {
        return (
            <div className="p-6 text-center bg-red-100 border border-red-400 text-red-700 rounded-lg">
                <p className="font-bold">Error Fetching Weather üõë</p>
                <p>{error}</p>
            </div>
        );
    }

    if (!weatherData) {
        return <div className="p-6 text-center text-gray-500">No weather data available.</div>;
    }

    // Dynamic icon URL from OpenWeatherMap
    const iconUrl = `https://openweathermap.org/img/wn/${weatherData.icon}@2x.png`;

    return (
        <div className="text-center p-8">
            <h2 className="text-3xl font-bold mb-4 text-indigo-600">
                Weather in {weatherData.city}, {weatherData.country}
            </h2>
            <div className="flex justify-center items-center my-6">
                <img src={iconUrl} alt={weatherData.description} className="w-20 h-20" />
                <p className="text-6xl font-extrabold text-gray-800 ml-4">
                    {weatherData.temperature}¬∞C
                </p>
            </div>
            <p className="text-2xl capitalize text-gray-600">
                **{weatherData.description}**
            </p>
        </div>
    );
}

export default WeatherModule;
