import { useState } from 'react';
import WeatherModule from './components/WeatherModule';
import CurrencyConverter from './components/CurrencyConverter';
import QuoteGenerator from './components/QuoteGenerator';
import './App.css'; 

const navItems = [
    { id: 'weather', name: 'Weather Info ðŸŒ¤ï¸' },
    { id: 'currency', name: 'Currency Converter ðŸ’±' },
    { id: 'quotes', name: 'Motivation Quote ðŸ’¬' },
];

function App() {
    const [activeTab, setActiveTab] = useState('weather');

    const renderModule = () => {
        switch (activeTab) {
            case 'weather':
                return <WeatherModule />;
            case 'currency':
                return <CurrencyConverter />;
            case 'quotes':
                return <QuoteGenerator />;
            default:
                return null;
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 p-4 sm:p-8">
            <header className="mb-8">
                <h1 className="text-4xl font-bold text-center text-indigo-700">
                    ByteXL InfoHub
                </h1>
                <p className="text-center text-gray-500 mt-1">
                    Your single-page utility center.
                </p>
            </header>

            <nav className="flex justify-center mb-8 space-x-2 sm:space-x-4">
                {navItems.map((item) => (
                    <button
                        key={item.id}
                        onClick={() => setActiveTab(item.id)}
                        className={`py-2 px-4 rounded-lg text-sm sm:text-base font-medium transition-colors duration-200 ${
                            activeTab === item.id
                                ? 'bg-indigo-600 text-white shadow-md'
                                : 'bg-white text-gray-700 hover:bg-gray-200 border border-gray-300'
                        }`}
                    >
                        {item.name}
                    </button>
                ))}
            </nav>

            <main className="max-w-4xl mx-auto bg-white p-6 rounded-xl shadow-2xl">
                {renderModule()}
            </main>

            <footer className="text-center text-gray-400 mt-10">
                &copy; 2025 InfoHub Challenge
            </footer>
        </div>
    );
}

export default App;
